mapper.delete:

delete from ecs_group_goods
where parent_id = #{parent_id,jdbcType=INTEGER}
  and goods_id = #{goods_id,jdbcType=INTEGER}
  and admin_id = #{admin_id,jdbcType=TINYINT}

mapper.update:

update ecs_group_goods
set goods_price = #{goods_price,jdbcType=DECIMAL}
where parent_id = #{parent_id,jdbcType=INTEGER}
  and goods_id = #{goods_id,jdbcType=INTEGER}
  and admin_id = #{admin_id,jdbcType=TINYINT}

mapper.select:

select parent_id, goods_id, admin_id, goods_price
from ecs_group_goods
where parent_id = #{parent_id,jdbcType=INTEGER}
  and goods_id = #{goods_id,jdbcType=INTEGER}
  and admin_id = #{admin_id,jdbcType=TINYINT}
