mapper.delete:

delete from ecs_link_goods
where goods_id = #{goods_id,jdbcType=INTEGER}
  and link_goods_id = #{link_goods_id,jdbcType=INTEGER}
  and admin_id = #{admin_id,jdbcType=TINYINT}

mapper.update:

update ecs_link_goods
set is_double = #{is_double,jdbcType=BIT}
where goods_id = #{goods_id,jdbcType=INTEGER}
  and link_goods_id = #{link_goods_id,jdbcType=INTEGER}
  and admin_id = #{admin_id,jdbcType=TINYINT}

mapper.select:

select goods_id, link_goods_id, admin_id, is_double
from ecs_link_goods
where goods_id = #{goods_id,jdbcType=INTEGER}
  and link_goods_id = #{link_goods_id,jdbcType=INTEGER}
  and admin_id = #{admin_id,jdbcType=TINYINT}
