mapper.delete:

delete from ecs_package_goods
where package_id = #{package_id,jdbcType=INTEGER}
  and goods_id = #{goods_id,jdbcType=INTEGER}
  and admin_id = #{admin_id,jdbcType=TINYINT}
  and product_id = #{product_id,jdbcType=INTEGER}

mapper.update:

update ecs_package_goods
set goods_number = #{goods_number,jdbcType=SMALLINT}
where package_id = #{package_id,jdbcType=INTEGER}
  and goods_id = #{goods_id,jdbcType=INTEGER}
  and admin_id = #{admin_id,jdbcType=TINYINT}
  and product_id = #{product_id,jdbcType=INTEGER}

mapper.select:

select package_id, goods_id, admin_id, product_id, goods_number
from ecs_package_goods
where package_id = #{package_id,jdbcType=INTEGER}
  and goods_id = #{goods_id,jdbcType=INTEGER}
  and admin_id = #{admin_id,jdbcType=TINYINT}
  and product_id = #{product_id,jdbcType=INTEGER}
