mapper.delete:

delete from ecs_supplier_goods_cat
where goods_id = #{goods_id,jdbcType=INTEGER}
  and cat_id = #{cat_id,jdbcType=SMALLINT}

mapper.insert:

insert into ecs_supplier_goods_cat (goods_id, cat_id, supplier_id
  )
values (#{goods_id,jdbcType=INTEGER}, #{cat_id,jdbcType=SMALLINT}, #{supplier_id,jdbcType=INTEGER}
  )

mapper.update:

update ecs_supplier_goods_cat
set supplier_id = #{supplier_id,jdbcType=INTEGER}
where goods_id = #{goods_id,jdbcType=INTEGER}
  and cat_id = #{cat_id,jdbcType=SMALLINT}

mapper.select:

select goods_id, cat_id, supplier_id
from ecs_supplier_goods_cat
where goods_id = #{goods_id,jdbcType=INTEGER}
  and cat_id = #{cat_id,jdbcType=SMALLINT}
