mapper.delete:

delete from ecs_volume_price
where price_type = #{price_type,jdbcType=BIT}
  and goods_id = #{goods_id,jdbcType=INTEGER}
  and volume_number = #{volume_number,jdbcType=SMALLINT}

mapper.update:

update ecs_volume_price
set volume_price = #{volume_price,jdbcType=DECIMAL}
where price_type = #{price_type,jdbcType=BIT}
  and goods_id = #{goods_id,jdbcType=INTEGER}
  and volume_number = #{volume_number,jdbcType=SMALLINT}

mapper.select:

select price_type, goods_id, volume_number, volume_price
from ecs_volume_price
where price_type = #{price_type,jdbcType=BIT}
  and goods_id = #{goods_id,jdbcType=INTEGER}
  and volume_number = #{volume_number,jdbcType=SMALLINT}
