mapper.delete:

delete from events_stages_history
where THREAD_ID = #{THREAD_ID,jdbcType=BIGINT}
  and EVENT_ID = #{EVENT_ID,jdbcType=BIGINT}

mapper.insert:

insert into events_stages_history (THREAD_ID, EVENT_ID, END_EVENT_ID, 
  EVENT_NAME, SOURCE, TIMER_START, 
  TIMER_END, TIMER_WAIT, WORK_COMPLETED, 
  WORK_ESTIMATED, NESTING_EVENT_ID, NESTING_EVENT_TYPE
  )
values (#{THREAD_ID,jdbcType=BIGINT}, #{EVENT_ID,jdbcType=BIGINT}, #{END_EVENT_ID,jdbcType=BIGINT}, 
  #{EVENT_NAME,jdbcType=VARCHAR}, #{SOURCE,jdbcType=VARCHAR}, #{TIMER_START,jdbcType=BIGINT}, 
  #{TIMER_END,jdbcType=BIGINT}, #{TIMER_WAIT,jdbcType=BIGINT}, #{WORK_COMPLETED,jdbcType=BIGINT}, 
  #{WORK_ESTIMATED,jdbcType=BIGINT}, #{NESTING_EVENT_ID,jdbcType=BIGINT}, #{NESTING_EVENT_TYPE,jdbcType=CHAR}
  )

mapper.select:

select THREAD_ID, EVENT_ID, END_EVENT_ID, EVENT_NAME, SOURCE, TIMER_START, TIMER_END, 
TIMER_WAIT, WORK_COMPLETED, WORK_ESTIMATED, NESTING_EVENT_ID, NESTING_EVENT_TYPE
from events_stages_history
where THREAD_ID = #{THREAD_ID,jdbcType=BIGINT}
  and EVENT_ID = #{EVENT_ID,jdbcType=BIGINT}
