mapper.delete:

delete from events_stages_summary_by_thread_by_event_name
where THREAD_ID = #{THREAD_ID,jdbcType=BIGINT}
  and EVENT_NAME = #{EVENT_NAME,jdbcType=VARCHAR}

mapper.update:

update events_stages_summary_by_thread_by_event_name
set COUNT_STAR = #{COUNT_STAR,jdbcType=BIGINT},
  SUM_TIMER_WAIT = #{SUM_TIMER_WAIT,jdbcType=BIGINT},
  MIN_TIMER_WAIT = #{MIN_TIMER_WAIT,jdbcType=BIGINT},
  AVG_TIMER_WAIT = #{AVG_TIMER_WAIT,jdbcType=BIGINT},
  MAX_TIMER_WAIT = #{MAX_TIMER_WAIT,jdbcType=BIGINT}
where THREAD_ID = #{THREAD_ID,jdbcType=BIGINT}
  and EVENT_NAME = #{EVENT_NAME,jdbcType=VARCHAR}

mapper.select:

select THREAD_ID, EVENT_NAME, COUNT_STAR, SUM_TIMER_WAIT, MIN_TIMER_WAIT, AVG_TIMER_WAIT, 
MAX_TIMER_WAIT
from events_stages_summary_by_thread_by_event_name
where THREAD_ID = #{THREAD_ID,jdbcType=BIGINT}
  and EVENT_NAME = #{EVENT_NAME,jdbcType=VARCHAR}
