mapper.delete:

delete from film_actor
where actor_id = #{actor_id,jdbcType=SMALLINT}
  and film_id = #{film_id,jdbcType=SMALLINT}

mapper.insert:

insert into film_actor (actor_id, film_id, last_update
  )
values (#{actor_id,jdbcType=SMALLINT}, #{film_id,jdbcType=SMALLINT}, #{last_update,jdbcType=TIMESTAMP}
  )

mapper.update:

update film_actor
set last_update = #{last_update,jdbcType=TIMESTAMP}
where actor_id = #{actor_id,jdbcType=SMALLINT}
  and film_id = #{film_id,jdbcType=SMALLINT}

mapper.select:

select actor_id, film_id, last_update
from film_actor
where actor_id = #{actor_id,jdbcType=SMALLINT}
  and film_id = #{film_id,jdbcType=SMALLINT}
