mapper.delete:

delete from gtid_executed
where source_uuid = #{source_uuid,jdbcType=CHAR}
  and interval_start = #{interval_start,jdbcType=BIGINT}

mapper.insert:

insert into gtid_executed (source_uuid, interval_start, interval_end
  )
values (#{source_uuid,jdbcType=CHAR}, #{interval_start,jdbcType=BIGINT}, #{interval_end,jdbcType=BIGINT}
  )

mapper.update:

update gtid_executed
set interval_end = #{interval_end,jdbcType=BIGINT}
where source_uuid = #{source_uuid,jdbcType=CHAR}
  and interval_start = #{interval_start,jdbcType=BIGINT}

mapper.select:

select source_uuid, interval_start, interval_end
from gtid_executed
where source_uuid = #{source_uuid,jdbcType=CHAR}
  and interval_start = #{interval_start,jdbcType=BIGINT}
