mapper.delete:

delete from innodb_index_stats
where database_name = #{database_name,jdbcType=VARCHAR}
  and table_name = #{table_name,jdbcType=VARCHAR}
  and index_name = #{index_name,jdbcType=VARCHAR}
  and stat_name = #{stat_name,jdbcType=VARCHAR}

mapper.update:

update innodb_index_stats
set last_update = #{last_update,jdbcType=TIMESTAMP},
  stat_value = #{stat_value,jdbcType=BIGINT},
  sample_size = #{sample_size,jdbcType=BIGINT},
  stat_description = #{stat_description,jdbcType=VARCHAR}
where database_name = #{database_name,jdbcType=VARCHAR}
  and table_name = #{table_name,jdbcType=VARCHAR}
  and index_name = #{index_name,jdbcType=VARCHAR}
  and stat_name = #{stat_name,jdbcType=VARCHAR}

mapper.select:

select database_name, table_name, index_name, stat_name, last_update, stat_value, 
sample_size, stat_description
from innodb_index_stats
where database_name = #{database_name,jdbcType=VARCHAR}
  and table_name = #{table_name,jdbcType=VARCHAR}
  and index_name = #{index_name,jdbcType=VARCHAR}
  and stat_name = #{stat_name,jdbcType=VARCHAR}
