mapper.delete:

delete from innodb_table_stats
where database_name = #{database_name,jdbcType=VARCHAR}
  and table_name = #{table_name,jdbcType=VARCHAR}

mapper.insert:

insert into innodb_table_stats (database_name, table_name, last_update, 
  n_rows, clustered_index_size, sum_of_other_index_sizes
  )
values (#{database_name,jdbcType=VARCHAR}, #{table_name,jdbcType=VARCHAR}, #{last_update,jdbcType=TIMESTAMP}, 
  #{n_rows,jdbcType=BIGINT}, #{clustered_index_size,jdbcType=BIGINT}, #{sum_of_other_index_sizes,jdbcType=BIGINT}
  )

mapper.update:

update innodb_table_stats
set last_update = #{last_update,jdbcType=TIMESTAMP},
  n_rows = #{n_rows,jdbcType=BIGINT},
  clustered_index_size = #{clustered_index_size,jdbcType=BIGINT},
  sum_of_other_index_sizes = #{sum_of_other_index_sizes,jdbcType=BIGINT}
where database_name = #{database_name,jdbcType=VARCHAR}
  and table_name = #{table_name,jdbcType=VARCHAR}

mapper.select:

select database_name, table_name, last_update, n_rows, clustered_index_size, sum_of_other_index_sizes
from innodb_table_stats
where database_name = #{database_name,jdbcType=VARCHAR}
  and table_name = #{table_name,jdbcType=VARCHAR}
