mapper.insert:

insert into memory_by_thread_by_current_bytes (thread_id, user, current_count_used, 
  current_allocated, current_avg_alloc, 
  current_max_alloc, total_allocated
  )
values (#{thread_id,jdbcType=BIGINT}, #{user,jdbcType=VARCHAR}, #{current_count_used,jdbcType=DECIMAL}, 
  #{current_allocated,jdbcType=LONGVARCHAR}, #{current_avg_alloc,jdbcType=LONGVARCHAR}, 
  #{current_max_alloc,jdbcType=LONGVARCHAR}, #{total_allocated,jdbcType=LONGVARCHAR}
  )
