mapper.delete:

delete from proxies_priv
where Host = #{host,jdbcType=CHAR}
  and User = #{user,jdbcType=CHAR}
  and Proxied_host = #{proxied_host,jdbcType=CHAR}
  and Proxied_user = #{proxied_user,jdbcType=CHAR}

mapper.update:

update proxies_priv
set With_grant = #{with_grant,jdbcType=BIT},
  Grantor = #{grantor,jdbcType=CHAR},
  Timestamp = #{timestamp,jdbcType=TIMESTAMP}
where Host = #{host,jdbcType=CHAR}
  and User = #{user,jdbcType=CHAR}
  and Proxied_host = #{proxied_host,jdbcType=CHAR}
  and Proxied_user = #{proxied_user,jdbcType=CHAR}

mapper.select:

select Host, User, Proxied_host, Proxied_user, With_grant, Grantor, Timestamp
from proxies_priv
where Host = #{host,jdbcType=CHAR}
  and User = #{user,jdbcType=CHAR}
  and Proxied_host = #{proxied_host,jdbcType=CHAR}
  and Proxied_user = #{proxied_user,jdbcType=CHAR}
