mapper.update:

update replication_applier_status
set SERVICE_STATE = #{SERVICE_STATE,jdbcType=CHAR},
  REMAINING_DELAY = #{REMAINING_DELAY,jdbcType=INTEGER},
  COUNT_TRANSACTIONS_RETRIES = #{COUNT_TRANSACTIONS_RETRIES,jdbcType=BIGINT}
where CHANNEL_NAME = #{CHANNEL_NAME,jdbcType=CHAR}

mapper.select:

select CHANNEL_NAME, SERVICE_STATE, REMAINING_DELAY, COUNT_TRANSACTIONS_RETRIES
from replication_applier_status
where CHANNEL_NAME = #{CHANNEL_NAME,jdbcType=CHAR}
