mapper.delete:

delete from session_account_connect_attrs
where PROCESSLIST_ID = #{PROCESSLIST_ID,jdbcType=BIGINT}
  and ATTR_NAME = #{ATTR_NAME,jdbcType=VARCHAR}

mapper.update:

update session_account_connect_attrs
set ATTR_VALUE = #{ATTR_VALUE,jdbcType=VARCHAR},
  ORDINAL_POSITION = #{ORDINAL_POSITION,jdbcType=INTEGER}
where PROCESSLIST_ID = #{PROCESSLIST_ID,jdbcType=BIGINT}
  and ATTR_NAME = #{ATTR_NAME,jdbcType=VARCHAR}

mapper.select:

select PROCESSLIST_ID, ATTR_NAME, ATTR_VALUE, ORDINAL_POSITION
from session_account_connect_attrs
where PROCESSLIST_ID = #{PROCESSLIST_ID,jdbcType=BIGINT}
  and ATTR_NAME = #{ATTR_NAME,jdbcType=VARCHAR}
