mapper.insert:

insert into slow_log (start_time, query_time, lock_time, 
  rows_sent, rows_examined, db, 
  last_insert_id, insert_id, server_id, 
  thread_id, user_host, sql_text
  )
values (#{start_time,jdbcType=TIMESTAMP}, #{query_time,jdbcType=TIME}, #{lock_time,jdbcType=TIME}, 
  #{rows_sent,jdbcType=INTEGER}, #{rows_examined,jdbcType=INTEGER}, #{db,jdbcType=VARCHAR}, 
  #{last_insert_id,jdbcType=INTEGER}, #{insert_id,jdbcType=INTEGER}, #{server_id,jdbcType=INTEGER}, 
  #{thread_id,jdbcType=BIGINT}, #{user_host,jdbcType=LONGVARCHAR}, #{sql_text,jdbcType=LONGVARBINARY}
  )
