  disk_tmp_tables, avg_tmp_tables_per_query, 
  tmp_tables_to_disk_pct, first_seen, last_seen, 
  digest, query, total_latency
  )
values (#{db,jdbcType=VARCHAR}, #{exec_count,jdbcType=BIGINT}, #{memory_tmp_tables,jdbcType=BIGINT}, 
  #{disk_tmp_tables,jdbcType=BIGINT}, #{avg_tmp_tables_per_query,jdbcType=DECIMAL}, 
  #{tmp_tables_to_disk_pct,jdbcType=DECIMAL}, #{first_seen,jdbcType=TIMESTAMP}, #{last_seen,jdbcType=TIMESTAMP}, 
  #{digest,jdbcType=VARCHAR}, #{query,jdbcType=LONGVARCHAR}, #{total_latency,jdbcType=LONGVARCHAR}
  )
