mapper.delete:

delete from tables_priv
where Host = #{host,jdbcType=CHAR}
  and Db = #{db,jdbcType=CHAR}
  and User = #{user,jdbcType=CHAR}
  and Table_name = #{table_name,jdbcType=CHAR}

mapper.update:

update tables_priv
set Grantor = #{grantor,jdbcType=CHAR},
  Timestamp = #{timestamp,jdbcType=TIMESTAMP},
  Table_priv = #{table_priv,jdbcType=CHAR},
  Column_priv = #{column_priv,jdbcType=CHAR}
where Host = #{host,jdbcType=CHAR}
  and Db = #{db,jdbcType=CHAR}
  and User = #{user,jdbcType=CHAR}
  and Table_name = #{table_name,jdbcType=CHAR}

mapper.select:

select Host, Db, User, Table_name, Grantor, Timestamp, Table_priv, Column_priv
from tables_priv
where Host = #{host,jdbcType=CHAR}
  and Db = #{db,jdbcType=CHAR}
  and User = #{user,jdbcType=CHAR}
  and Table_name = #{table_name,jdbcType=CHAR}
