mapper.update:

update user_defined_functions
set UDF_RETURN_TYPE = #{UDF_RETURN_TYPE,jdbcType=VARCHAR},
  UDF_TYPE = #{UDF_TYPE,jdbcType=VARCHAR},
  UDF_LIBRARY = #{UDF_LIBRARY,jdbcType=VARCHAR},
  UDF_USAGE_COUNT = #{UDF_USAGE_COUNT,jdbcType=BIGINT}
where UDF_NAME = #{UDF_NAME,jdbcType=VARCHAR}

mapper.select:

select UDF_NAME, UDF_RETURN_TYPE, UDF_TYPE, UDF_LIBRARY, UDF_USAGE_COUNT
from user_defined_functions
where UDF_NAME = #{UDF_NAME,jdbcType=VARCHAR}
