mapper.delete:

delete from user_variables_by_thread
where THREAD_ID = #{THREAD_ID,jdbcType=BIGINT}
  and VARIABLE_NAME = #{VARIABLE_NAME,jdbcType=VARCHAR}

mapper.insert:

insert into user_variables_by_thread (THREAD_ID, VARIABLE_NAME, VARIABLE_VALUE
  )
values (#{THREAD_ID,jdbcType=BIGINT}, #{VARIABLE_NAME,jdbcType=VARCHAR}, #{VARIABLE_VALUE,jdbcType=LONGVARBINARY}
  )

mapper.update:

update user_variables_by_thread
set VARIABLE_VALUE = #{VARIABLE_VALUE,jdbcType=LONGVARBINARY}
where THREAD_ID = #{THREAD_ID,jdbcType=BIGINT}
  and VARIABLE_NAME = #{VARIABLE_NAME,jdbcType=VARCHAR}

mapper.select:

select THREAD_ID, VARIABLE_NAME, VARIABLE_VALUE
from user_variables_by_thread
where THREAD_ID = #{THREAD_ID,jdbcType=BIGINT}
  and VARIABLE_NAME = #{VARIABLE_NAME,jdbcType=VARCHAR}
