mapper.insert:

insert into x$user_summary (user, statements, statement_latency, 
  statement_avg_latency, table_scans, file_ios, 
  file_io_latency, current_connections, total_connections, 
  unique_hosts, current_memory, total_memory_allocated
  )
values (#{user,jdbcType=VARCHAR}, #{statements,jdbcType=DECIMAL}, #{statement_latency,jdbcType=DECIMAL}, 
  #{statement_avg_latency,jdbcType=DECIMAL}, #{table_scans,jdbcType=DECIMAL}, #{file_ios,jdbcType=DECIMAL}, 
  #{file_io_latency,jdbcType=DECIMAL}, #{current_connections,jdbcType=DECIMAL}, #{total_connections,jdbcType=DECIMAL}, 
  #{unique_hosts,jdbcType=BIGINT}, #{current_memory,jdbcType=DECIMAL}, #{total_memory_allocated,jdbcType=DECIMAL}
  )
